  [ { &#34;id&#34;: &#34;7a62a2d3.c0f4a4&#34;, &#34;type&#34;: &#34;Subflow&#34;, &#34;name&#34;: &#34;Send Automation Speech Notification&#34;, &#34;info&#34;: &#34;&#34;, &#34;category&#34;: &#34;&#34;, &#34;in&#34;: [ { &#34;x&#34;: 60, &#34;y&#34;: 100, &#34;wires&#34;: [ { &#34;id&#34;: &#34;87411254.a6ed18&#34; } ] } ], &#34;out&#34;: [ { &#34;x&#34;: 1120, &#34;y&#34;: 100, &#34;wires&#34;: [ { &#34;id&#34;: &#34;d44cc51a.be0668&#34;, &#34;port&#34;: 0 } ] } ], &#34;status&#34;: { &#34;x&#34;: 220, &#34;y&#34;: 40, &#34;wires&#34;: [ { &#34;id&#34;: &#34;aef53056.742438&#34;, &#34;port&#34;: 0 } ] } }, { &#34;id&#34;: &#34;89300b1.595bcf8&#34;, &#34;type&#34;: &#34;function&#34;, &#34;z&#34;: &#34;7a62a2d3.c0f4a4&#34;, &#34;name&#34;: &#34;Set Speech Payload&#34;, &#34;func&#34;: &#34;const entity = flow.get(\&#34;$parent.speech_entity_id\&#34;) || (msg.payload &amp;&amp; msg.payload.entity_id) || \&#34;media_player.office_echo_plus\&#34;;
