 #include &lt;stdio.h&gt;#include &lt;stdlib.h&gt;#include &lt;string.h&gt;#include &lt;unistd.h&gt; volatile  unsigned  int  a  =  0  -  1 ; volatile  u_int8_t  *  z  =  NULL ; int  main ()  {  switch ( a )  {  case  0  -  1 :  z  =  aligned_alloc ( sysconf ( _SC_LEVEL1_DCACHE_LINESIZE ),  1  *  10  +  3 );  break ;  case  1  *  10  +  3 :  memcpy ( z  +  3 ,  z  +  2 ,  1 );  memcpy ( z  +  9 ,  z  +  3 ,  1 );  memcpy ( z  +  7 ,  z  +  4 ,  1 );  printf ( &#34;%s&#34; ,  z );  free ( z );  return  0 ;  default:  z [ a ]  =  a  ==  0  ?  z [ a ]  =  ( 1  &lt;&lt;  ( a  +  6 ))  |  ( 1  &lt;&lt;  ( a  +  3 ))  :  a  ==  1  ?  ( z [ a  -  1 ]  &amp;  ( 1  &lt;&lt;  ( a  +  5 )))  |  ( z [ a  -  1 ]  &gt;&gt;  a )  |  ( z [ a  -  1 ]  &gt;&gt;  ( a  +  5 ))  :  a  ==  2  ?  z [ a  -  2 ]  |  z [ 1 ]  &amp;  ~ ( z [ a  -  2 ]  &gt;&gt;  ( a  +  4 ))  :  a  ==  4  ?  z [ a  -  2 ]  |  z [ a  -  3 ]  |  ( z [ a  -  4 ]  &gt;&gt;  ( a  +  1 ))  :  a  ==  5  ?  ( z [ a  -  4 ]  &amp;  z [ a  -  3 ]  &amp;  z [ a  -  5 ])  &gt;&gt;  ( a  -  4 )  :  a  ==  6  ?  ( z [ a  -  2 ]  &amp;  ~ z [ a  -  1 ]  |  ( z [ a  -  6 ]  &gt;&gt;  ( a  -  4 )))  &amp;  ~ ( z [ a  -  6 ]  &gt;&gt;  ( a  -  2 )  &lt;&lt;  ( a  -  5 ))  :  a  ==  8  ?  ( ~ z [ a  -  7 ]  &amp;  z [ a  -  2 ])  |  z [ a  -  3 ]  |  z [ a  -  3 ]  &lt;&lt;  ( a  -  7 )  :  a  ==  10  ?  z [ a  -  9 ]  &amp;  ~ ( z [ a  -  5 ]  &gt;&gt;  ( a  -  5 ))  :  a  ==  11  ?  z [ a  -  6 ]  |  (( z [ a  -  1 ]  &amp;  z [ a  -  3 ])  &gt;&gt;  ( a  -  5 ))  :  a  -  a ;  }  a  =  a  +  1 ;  main (); }
