        module. exports  = {  webpack( config) {  const {  MordredWebpackPlugin }  =  require(  &#39;mordred/webpack &#39;)  const  mordredPlugin  =  new  MordredWebpackPlugin()  config. plugins. push(mordredPlugin)  return config },}
  module. exports  = { plugins : [ { resolve :   &#39;mordred-source-filesystem &#39;, options : {   // This is where you&#39;ll be creating Markdown files path :  __dirname  +   &#39;/content &#39;, }, }, { resolve :   &#39;mordred-transformer-markdown &#39;, }, ],}
      When you run  next or  next build, Mordred will generate a GraphQL client at  mordred/graphql.js, then you can use the generated client to query data.
  import {  query,  gql }  from   &#39;../mordred/graphql &#39; export  const  getStaticProps  =  async ()  =&gt; {  const {  data,  errors }  =  await  query(  gql `  {   allMarkdown {   nodes {   id   slug   createdAt   updatedAt   html   frontmatter {   # ... or any frontmatter   # like:   title  }  }  }  }   `)  if (errors) {  throw errors[ 0] }  return { props : {  ...data, }, }} export  default ({ allMarkdown })  =&gt; {  return (  &lt;ul &gt; { allMarkdown. nodes. map(( post)  =&gt; {  return (  &lt;li key ={ post. id} &gt;  &lt;Link href ={  `/post/  ${ post. slug } `} &gt;{ post. title} &lt; /Link &gt;  &lt; /li &gt; ) })}  &lt; /ul &gt; )}
   import  express  from   &#39;express &#39; import  graphqlHTTP  from   &#39;express-graphql &#39; import {  schema }  from   &#39;../../mordred/graphql &#39; const  app  =  express() app. use(  graphqlHTTP({ schema, graphiql :  true, })) export  default  app
